import { createContext, useEffect, useState } from "react";
import axios from "axios";
import Swal from "sweetalert2";
import { useNavigate } from "react-router-dom";

export const AuthContext = createContext({});

const AuthProvider = ({ children }) => {

  //utilizando o useEffect para montagem
  useEffect(() => {}, []);
  const [user, setUser] = useState({});
  const navigate = useNavigate();

  //pega meus dados, dá os alertas necessários e depois, em caso de erro, me mostra os erros e os alertas.
  const userRegister = (data) => {
    axios
      .post("https://kenziehub.herokuapp.com/users", data)
      .then((response) => {
        console.log(response);

        Swal.fire(
          "Usuário cadastrado com sucesso",
          "redirecionando...",
          "success"
        );
        navigate("/");
      })
      .catch(
        Swal.fire({
          icon: "error",
          title: "Oops...",
          text: "Email ja castrado!",
        })
      );
  };
  const addUser = (data) => {
    axios
      .post("https://kenziehub.herokuapp.com/sessions", data)
      .then((response) => {
        setUser(response);
        window.localStorage.clear();
        window.localStorage.setItem("authToken", response.data.token);
        window.localStorage.setItem("userName", response.data.user.name);
        Swal.fire("Você entrou com sucesso!",
        "Redirecionando usuário...",
        "Successo!");
        navigate("/dashboard");
      })
      .catch((err) => {
        console.log(err);
        Swal.fire({
            icon: "error",
            title: "Eita!",
            text: "Você não possui cadastro! =(",
        });
      });
      console.log(data)
  };

  return (
    <AuthContext.Provider value={{ addUser, userRegister, user }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthProvider
