import {createContext, useEffect, useState} from "react"
import {useNavigate} from "react-router-dom"
import api from "../services/api"

//segui a demo, mas não toh entendendo nada. 
export const AuthContext = createContext({})

const AuthProvider = ({children}) => {
    const [user, setUser] = useState(null)
    const [loading, setLoading] = useState(true)
    const navigate = useNavigate()

    useEffect(() => {
        async function loadUser(){
            const token = localStorage.getItem("@context-demo:token")

            if(token){
                try{
                    api.defaults.headers.authorization = `Bearer ${token}`

                    const {data} = await api.get("/users")
                    console.log("busquei usuario", data)

                    setUser(data)
                } catch(error){
                    console.log(error)
                }   
            }
            setLoading(false)
        }
        loadUser()
    })

    const sigIn = async (data) => {

        const response = await api.post("/sessions", data)

        console.log(response)

        const {user: userResponse, token} = response.data
        api.defaults.headers.authorization = `Bearer ${token}`

        setUser(userResponse)

        localStorage.setItem("@context-demo:token", token)
        navigate("/dashboard", {replace: true})
    }
    return (
        <AuthContext.Provider value={{user, sigIn, loading}}>
            {children}
        </AuthContext.Provider>
    )
}

export default AuthProvider
